def render_header(logo_path, header_path):
    col1, col2, col3 = st.columns(3)
    col3.image(logo_path, width=200)

    st.write("###")

    ## HEADER STARTSEITE
    header_img = load_pil_image(header_path)
    if not st.session_state.get("show_results"):
        st.image(header_img)

def render_layout(page_name=None):
    if page_name:
        st.session_state.current_page = page_name

    inject_css()
    if page_name == "index":
        render_header(
            "assets/datadish_logo_light.png",
            "assets/header.png",
        )
